import './App.css';
import { useState } from 'react';
import storage from './firebase';


function App() {
  const [image, setImage] = useState('');
  const [show, setShow] = useState(false);
  const [value, setValue] = useState('');



  const handleSubmit = (e) => {
    e.preventDefault();

    if (image == null)
      return;
//here I am sending the file to the firebase storage
      storage.ref(`/images/${image.name}`).put(image)
      .on("state_changed", alert("success"), alert, () => {
        // getting meta-data of file
        storage.ref("images").child(image.name).getMetadata().then((data)=> {
          setValue(data);
          setDisplay(true);
        })
      });

    console.log("hello World")
  };




  return (
    < div className="App">
      <h2>
      When you see me
      </h2>

      <center>
        <input type="file" placeholder="choose the file" onChange={(e)=>{setImage(e.target.files[0])}}></input><br />
        <button onClick={handleSubmit}>upload File</button>


        show ?
        <div>
           react
              <h2>Name : {value.name}</h2>
              <h2>Size : {value.size}</h2>
              <h2>Path : {value.fullpath}</h2>
              <h2>Time : {value.timeCreated}</h2>
            </div>



          </center>

    </div>
  );
}

export default App;
