import './App.css';
import { useState } from 'react';
import Storage from './firebase';


function App() {
  const [image, setFile] = useState('');
  const [display, setDisplay] = useState(false);
  const [value, setValue] = useState('');



  const handleSubmit = (e) => {
    e.preventDefault();

    if (image == null)
      return;
// here we are I am sending the file to the firebase storage
      Storage.ref(`/images/${image.name}`).put(image).on("state_changed", alert("sucess"),
      alert, () => {
        // getting meta-data of file
        Storage.ref("images").child(image.name).getMetadata().then((data)=> {
          setValue(data);
          setDisplay(true);
        })
      });

    console.log("hello World")
  };




  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <input type="file" placeholder="choose the file" onChange={(e)=>{setFile(e.target.files[0])}}></input><br />
        <button>upload File</button>
      </form>
<div>
      {
          display ?
            <div>
              <h2>Name : {value.name}</h2>
              <h2>Size : {value.size}</h2>
              <h2>Path : {value.fullpath}</h2>
              <h2>Time : {value.timeCreated}</h2>
            </div>
            :
            <div></div>
        }
        </div>
    </div>
  );
}

export default App;
