import './App.css';
import { useState } from 'react';



function App() {
  const [image, setImage] = useState('');
  const [show, setShow] = useState(false);
  const [value, setValue] = useState('');



  const handleSubmit = (e) => {
    e.preventDefault();
    let data = document.getElementsByName("file")[0].files["0"];

    const { name,size,path,time} = data;
    if(name && size && path && time ){
      setImage([
        ...data,
        {
          name: data.name,
          size: data.size,
          path: data.path,
          time: data.time,
        },
      ]);
    }
    console.log('angy',data.name);
  }








  return (
    < div className="App">


      <center>
        <form onSubmit={handleSubmit}>
        <input type="file" name="file" placeholder="choose the file" />
        <button >upload File</button>
        <br /><br /><br /><br />
      </form>
        <div>

              <h2>Name : {value.name}</h2>
              <h2>Size : {value.size}</h2>
              <h2>Path : {value.fullpath}</h2>
              <h2>Time : {value.timeCreated}</h2>
            </div>

            <div></div>



          </center>

    </div>
  );
}

export default App;
